import React from "react";
import "./Cart.css";
import { useSelector } from "react-redux";
const Cart = () => {
  const quantity = useSelector(state => state.Cart.tot);
  return (
    <div className="cartIcon">
      <h3>Cart: {quantity} Items</h3>
    </div>
  );
};

export default Cart;
